import React, { Component } from "react";
import ReactDOM, { render } from "react-dom";
import { GraphData } from "./graphData";
class Legion extends Component {
  state = {
    colorCodes: GraphData.colorCodes
  };

  render() {
    const configData = this.getConfig();
    return (
      <div className="legion-wrapper">
        <ul>
          {configData.map((config) => (
            <li>
              <span style={config.style} className="color-palette"></span>
              <span>{config.name}</span>
            </li>
          ))}
        </ul>
      </div>
    );
  }

  getConfig() {
    const graphData = GraphData.populateGraphData();
    const depth = graphData.maxDepth;
    const legionName = graphData.legionData;
    const config = [];
    for (let i = 1; i <= depth; i++) {
      config.push({
        name: legionName[i - 1],
        style: {
          backgroundColor: this.state.colorCodes[i]
            ? this.state.colorCodes[i]
            : this.state.colorCodes[i % this.state.colorCodes.length]
        }
      });
    }
    return config;
  }
}

export default Legion;
