import React from "react";
import styled from "styled-components";
import Tree from "react-d3-tree";
import { GraphData } from "./graphData";
import { render } from "react-dom";
import Legion from "./legion";

import "./App.css";

const CharDiv = styled.div`
width: 100vw;
height: 100vh;
padding: 60px;

.node__root > circle {
    fill: blue;
    r: 30;
    border-radius: 3px;
  }

  .node__branch > circle {
    fill: yellow;
  }

  .node__leaf > circle {
    fill: green
    /* Let's also make the radius of leaf nodes larger */
    r: 40;
  }
`;

const sizeData = { x: 450, y: 700 };

export const Home = ({ token }) => {
  // This is a simplified example of an org chart with a depth of 2.
  // Note how deeper levels are defined recursively via the `children` property.
  const mapData = GraphData.populateGraphData(getBinaryTree);

  async function getTree() {
    return fetch("http://localhost:8080/api/get_child_node_users", {
      method: "GET",
      headers: {
        Authorization: "Bearer " + token.token,
        "Content-Type": "application/x-www-form-urlencoded",
        "Access-Control-Allow-Origin": "*",
      },
    }).then((data) => data.json());
  }

  const getBinaryTree = async () => {
    let constructTree = { eagleViewRequestDocument: {} };
    let unPlaced = [];

    const data = await getTree();

    if (Array.isArray(data)) {
      for (let i = 1; i < data.length; i++) {
        if (data[i - 1].position === "0") {
          constructTree["eagleViewRequestDocument"]["1"] =
            data[i - 1].user_name;
        } else if (data[i - 1].position === "L") {
          constructTree["eagleViewRequestDocument"][data[i - 1].father + ".1"] =
            data[i - 1].user_name;
        } else if (data[i - 1].position === "R") {
          constructTree["eagleViewRequestDocument"][data[i - 1].father + ".2"] =
            data[i - 1].user_name;
        } else {
          unPlaced.push(data[i - 1]);
        }
      }

      console.log("THE TREE : " + JSON.stringify(constructTree));
      console.log("UNPLACED : " + JSON.stringify(unPlaced));
    }
  };

  const straightPathFunc = (linkDatum, orientation) => {
    const { source, target } = linkDatum;
    return orientation === "horizontal"
      ? `M${source.y},${source.x}L${target.y},${target.x}`
      : `M${source.x},${source.y}L${target.x},${target.y}`;
  };

  const renderNodeWithCustomEvents = ({ nodeDatum, toggleNode }) => {
    return (
      <g>
        <foreignObject x="0" height="120px" width="500px" y="-60px">
          <div
            title={nodeDatum.fullName}
            className="elemental-node"
            style={nodeDatum.style}
          >
            <span title={nodeDatum.fullName} className="elemental-name">
              {nodeDatum.shortName}
            </span>
            {nodeDatum.fullName === false && (
              <div className="elemental-node--hover">
                <span>{nodeDatum.fullName}</span>
              </div>
            )}
          </div>
        </foreignObject>
      </g>
    );
  };

  return (
    // `<Tree />` will fill width/height of its container; in this case `#treeWrapper`.
    <React.Fragment>
      <div className="graph-legion">
        <Legion />
      </div>
      <div className="graph">
        <Tree
          data={mapData.graph}
          renderCustomNodeElement={(rd3tProps) =>
            renderNodeWithCustomEvents({ ...rd3tProps })
          }
          orientation={"verticle"}
          translate={mapData.translate}
          zoom={mapData.defaultZoom}
          pathFunc={straightPathFunc} //"eblow"
          nodeSize={sizeData}
          scaleExtent={{ min: mapData.defaultZoom, max: 1 }}
          separation={{ siblings: 1, nonSiblings: 1.5 }}
        />
      </div>
    </React.Fragment>
  );
};
